import countries from 'world-countries'
import CountryInfo from './CountryInfo'
import styled from 'styled-components'

// const countryBlock = styled.div`

// `

// Style
const test = styled.div`
    width: 40vw;
    margin: 20pt;
    padding: 2pt 16pt;
    border-radius: 5pt;
    box-shadow: 0px 0px 5px 5px rgba(0,0,0,0.2);
    border-radius: 7pt;
    transition: .2s;

    &:hover {
        box-shadow: 0px 0px 10px 8px rgba(0,0,0,0.3);
    }
`

const App = (props) => {
  const sortedCountries = countries.sort((a, b) => b.area - a.area).filter(country => country.name.common !== 'Antarctica').slice(0, 15)

  return (
    <test>
      {sortedCountries.map((country, index) => <CountryInfo country={country} detailed={index < 5} key={country.cca3} largest={sortedCountries[0].area} />)}
    </test>
  )
}

export default App
