import './CountryInfo.css'

const CountryInfo = ({ country }) => {
  const russiaArea = 17098242
  const ratio = country.area / russiaArea * 100

  return (
    <div className='country'>
      <p className='countryName'>Country: {country.name.common}</p>
      <p>Area: {country.area} km<var><sup>2</sup></var></p>
      <div className='areaBar' style={{ width: ratio + '%' }} />
    </div>
  )
}

export default CountryInfo
