import countries from 'world-countries'
import CountryInfo from './CountryInfo'

const country = {
  display: 'flex',
  flexDirection: 'row'
  // alignItems: 'center'
}

const App = (props) => {
  countries.sort((a, b) => b.area - a.area)
  const bigCountries = countries.filter(country => country.name.common !== 'Antarctica').slice(0, 5)
  const smallCountries = countries.filter(country => country.name.common !== 'Antarctica').slice(5, 15)

  return (
    <div style={country}>
      {bigCountries.map(country => <CountryInfo detalied key={country.cca3} country={country} />)}
      {smallCountries.map(country => <CountryInfo detalied={false} key={country.cca3} country={country} />)}
    </div>
  )
}

export default App
