const CountryInfo = ({ country }) => {
  // Constants
  const russiaArea = 17098242
  const ratio = country.area / russiaArea * 100

  // Style
  const countryStyle = {
    backgroundColor: 'darkslategrey',
    color: 'white',
    margin: '20pt',
    padding: '4pt',
    borderRadius: '5pt'
  }

  const areaBar = {
    height: '10pt',
    backgroundColor: '#fff',
    marginBottom: '6pt',
    width: ratio + '%'
  }

  const countryName = {
    fontWeight: 'bold'
  }

  const num = 1

  return (
    <div style={countryStyle}>
      <p style={countryName}>Country: {country.name.common}</p>

      {num > 0 &&
        <p>Capital: {country.capital}</p>,
        <p>Region: {country.subregion}</p>}

      <p>Area: {country.area} km<sup>2</sup></p>
      <div className='areaBar' style={areaBar} />

    </div>
  )
}

export default CountryInfo
