const CountryInfo = ({ country, detailed, largest }) => {
  // Constants magic varible
  const ratio = country.area / largest * 100

  // Style
  const countryStyle = {
    backgroundColor: 'darkslategrey',
    color: 'white',
    margin: '20pt',
    padding: '4pt',
    borderRadius: '5pt',
    width: '50vw'
  }

  const areaBar = {
    height: '10pt',
    backgroundColor: '#fff',
    marginBottom: '6pt',
    width: ratio + '%'
  }

  return (
    <div style={countryStyle}>

      <p> <b>{country.flag} {country.name.common}</b> â€¢ {Math.round(country.area / 100000) / 10} million km<sup>2</sup></p>
      <div className='areaBar' style={areaBar} />

      {/* Conditional Rendering */}
      {detailed &&
        <div>
          <p>Capital: {country.capital}</p>
          <p>Region: {country.subregion}</p>
        </div>}

    </div>
  )
}

export default CountryInfo
