import styled from 'styled-components'

// Style
const Country = styled.div`
    width: 30vw;
    margin: 10pt;
    padding: 1pt 8pt;
    border-radius: 5pt;
    box-shadow: 0px 0px 5px 5px rgba(0,0,0,0.2);
    border-radius: 7pt;
    transition: .2s;

    &:hover {
        box-shadow: 0px 0px 10px 8px rgba(0,0,0,0.3);
    }
`

const Bar = styled.div`
    width: ${props => props.width};
    height: 10pt;
    background-color: #3F66A6;
    margin-bottom: 10pt;
    border-radius: 3pt;
`

const DetailedStyle = styled.p`
    margin: '8pt 0';
`

const CountryInfo = ({ country, detailed, largest }) => {
  // Constants
  const ratio = country.area / largest * 100

  return (
    <Country>

      <p> <b>{country.flag} {country.name.common}</b> â€¢ {Math.round(country.area / 100000) / 10} million km<sup>2</sup></p>
      <Bar width={ratio + '%'} />

      {/* Conditional Rendering */}
      {detailed &&
        <div>
          <DetailedStyle>Capital: {country.capital}</DetailedStyle>
          <DetailedStyle>Region: {country.subregion}</DetailedStyle>
          <DetailedStyle>Region: {country.subregion}</DetailedStyle>
        </div>}

    </Country>
  )
}

export default CountryInfo
