import countries from 'world-countries'
import CountryInfo from './CountryInfo'
import styled from 'styled-components'
import { useState } from 'react'

// Style
const Container = styled.div`
  position: absolute;
  top: 80pt;

  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
`

const App = (props) => {
  const sortedCountries = countries.sort((a, b) => b.area - a.area).filter(country => country.name.common !== 'Antarctica').slice(0, 15)
  const [searchString, setSearchString] = useState('')

  const handleChange = (e) => {
    setSearchString(e.target.value)
  }

  console.log(searchString)

  const newSoretedCountries = sortedCountries.filter(country => country.name.common === searchString)

  const matchText = props => {
    const word = props.name.common
    const lowerCaseWord = word.toLowerCase()
    const lowerCaseSearchString = searchString.toLowerCase()

    return lowerCaseWord.indexOf(lowerCaseSearchString) === 0
  }

  return (
    <Container>
      <input onChange={handleChange} placeholder='Search country...' />
      {newSoretedCountries.map((country, index) => <CountryInfo country={country} detailed={index < 6} key={country.cca3} largest={sortedCountries[0].area} />)}
    </Container>
  )
}

export default App
